const initialState = {
    data: {},
    current: "",
};

  
export default function (state = initialState, action){

    if (!action.hasOwnProperty('payload') || !action){
        action = {payload:{}};
    } 
    switch (action.type){
        case 'FETCH_QUOTES':
            return {
                data: Object.assign({}, state.data, action.payload),
                current: state.current
            };
        case 'NEW_QUOTE':
            return {
                data: state.data,
                current: action.payload
            };
        default:
            return state;
    }
}
