import React, {Component} from 'react';
import '../stylesheets/style.css';
import {setNewImage} from '../actions/index';
import { bindActionCreators } from 'redux';

export default class CharacterCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            loading: true,
            image: props.image,
            name: props.name
        };
    }



    render(){
        let image = new Image();
        image.src = this.state.image;
        let styles;
        image.onload = ()=>{
            styles = {
                width: image.height / 20 + "px",
                height: image.width / 20 + "px"
            }
            this.state.loading = false;                                 
        };

        
    
        if (this.state.loading){
            return (<img src={this.state.image} style={styles}></img>);
        } else {
            return (<img src={this.state.image} style={styles}></img>);
        }


    }
}
