import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import ReactCardFlip from 'react-card-flip';
import '../stylesheets/style.css';

export default class CharacterCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            isFlipped: false,
            isHovered: false,
            url: props.image,
            name: props.name,
            quotes: props.quotes,
            self: null
        };
        this.handleHover = this.handleHover.bind(this);
        this.getHoverStyle = this.getHoverStyle.bind(this);
        this.flip = this.flip.bind(this);
        this.unflip = this.unflip.bind(this);
        this.getZIndex = this.getZIndex.bind(this);
    }

    render(){
        return (            
            <ReactCardFlip  isFlipped={this.state.isFlipped} containerStyle={this.getZIndex()} flipDirection="horizontal">
                {this.getFrontCard(this.state.name, this.state.url)}
                {this.getBackCard(this.state.quote)}
            </ReactCardFlip>
        );
    }


    componentDidMount(){
        this.setState(prevState => ({
            self: ReactDOM.findDOMNode(this),
            quote: prevState.quotes[Math.floor(Math.random() * prevState.quotes.length)]
        }));
    }
    
    handleHover(){
        this.setState(prevState => ({
            isHovered: !prevState.isHovered,
            zIndex: -prevState.zIndex
        }));
    }

    getZIndex(){
        if (this.state.isHovered || this.state.isFlipped){
            return {
                zIndex:1};
        } else {
            return {};
        }
    }
    
    getHoverStyle(){
        if (this.state.isHovered && !this.state.isFlipped) {
            return  {
                transition: 'transform .2s',
                transform: 'scale(1.2)',
            }
        } else {
            return {
                transition: 'transform .2s', 
                transform: 'scale(1)' 
            }
        }
    }

    flip(e){
        e.preventDefault();
        this.setState(prevState => ({isFlipped: true}));
        let elem = this.state.self.style;
        let width= 17.15;
        let height= 20.5;
        let id = setInterval(frame, 5);
        function frame() {
          if (width >= 69 && height >= 41.5) {
            clearInterval(id);
          } else {
            width += width >= 69 ? 1 : 0;
            height += height >= 41.5 ? .5 :0;
            elem.height = width + 'vw';
            elem.width = height + 'vw';
          }
        }
    }

    unflip(e){
        e.preventDefault();
        this.setState(prevState => ({isFlipped: false}));
        let elem = this.state.self.style;
        let width= 69;
        let height= 41.5;
        let id = setInterval(frame, 1000);
        function frame() {
          if (width <= 16 && height <= 20.5) {
            clearInterval(id);
          } else {
            width -= width <= 16 ? 1 : 0;
            height -= height <= 20.5 ? .5 :0;
            elem.height = width + 'vw';
            elem.width = height + 'vw';
          }
        }
    }


    getFrontCard(alt, src){
        return ( 
        <div key='front' onMouseEnter={this.handleHover} onMouseLeave={this.handleHover} style={this.getHoverStyle()} onClick={this.flip}>
            <img alt={alt} src={src} id='characterImage'></img>
        </div>
        );
    }

    getBackCard(quote){
        return (
        <div id='back' key='back' onClick={this.unflip}>
        </div>
        );
    }
}
