import React, {Component} from 'react';
import '../stylesheets/style.css';

export default class CharacterCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            loading: true,
            url: props.image,
            name: props.name
        };
    }



    render(){
        let image = new Image();
        image.src = this.state.url;
        let styles = {
            width: image.width / 35 + "vw",
            height: image.height / 35 + "vw"
        };
       
        return (
            <div className="card">
                <div className="front">
                    <img src={this.state.url} style={styles}></img>
                </div>
                <div className="back" style={styles}>
                    <h1>Dwight Schrute</h1>
                </div>
            </div>
        );
    }
}
