const initialState = {
    key: "Andy",
    cardsFlipState: {
        Andy: false,
        Michael: false,
        Pam: false,
        Dwight: false,
        Jim: false,
        Kelly: false,
        Angela: false,
        Stanley: false
    }
}

  
export default function (state = initialState, action){
    switch (action.type){
        case 'FLIP_CARD':
           
            let newFlipState = state.cardsFlipState;
            newFlipState[action.payload] = !newFlipState[action.payload];
        
            return {
                key: state.key,
                cardsFlipState: newFlipState
            };
        case 'SELECT':
            return {
                key: action.payload,
                cardsFlipState: state.cardsFlipState
            };
        default:
            return state;
    }
}
