import React, {Component} from 'react';
import ReactCardFlip from 'react-card-flip';
import '../stylesheets/loadingScreen.css';


export default class Card extends Component {
    constructor(props){
        super(props);
        this.state = {
            isFlipped: false
        };
        this.handleClick.bind(this);
        this.self = React.createRef();
    }

    render(){
        return (
            <ReactCardFlip isFlipped = {this.state.isFlipped} onClick={this.handleClick}> 
                <div key = 'front'>
                    {this.props.front}
                </div>
                <div key = 'back' className='.emptyBack'>
                    {setTimeout(() => this.props.back, 2000)}
                </div>
            </ReactCardFlip>
        )
    }

    handleClick(){
        this.setState(prevState => {{
            isFlipped: !prevState.isFlipped
        }})
        this.props.flip(this);
    }
}
