import React, {Component} from 'react';
import ReactCardFlip from 'react-card-flip';
import '../stylesheets/style.css';

export default class CharacterCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            isFlipped: false,
            isHovered: false,
            url: props.image,
            name: props.name,
            positionX: null,
            positionY: null
        };
        this.handleClick = this.handleClick.bind(this);
        this.handleHover = this.handleHover.bind(this);
        this.myRef = React.createRef();
    }

    handleClick(e) {
        e.preventDefault();
        this.setState(prevState => (
            { isFlipped: !prevState.isFlipped }
        ));
    }    
    handleHover(){
        this.setState(prevState => ({
            isHovered: !prevState.isHovered
        }));
    }

    render(){
        let imgClass = "hover";
        if (!this.state.isHovered  ){
            imgClass = "unhover";
        } 

        let styles = {};
        if (this.state.isFlipped){
            styles = {
                transition: 'transform .2s',
                transform: 'translate(0vw, 1vh)',
            };
        }
    
        return (
            <div ref={this.myRef} onClick = {this.calcPosition.bind(this)} style={styles} onMouseEnter={this.handleHover} onMouseLeave={this.handleHover}>
                <ReactCardFlip  isFlipped={this.state.isFlipped} flipDirection="horizontal">
                    <div key='front' onClick={this.handleClick} >
                        <img alt={this.state.name} src={this.state.url} id='characterImage'></img>
                    </div>
                    <div id='back' key='back' onClick={this.handleClick}>
                        <h1>Dwight</h1>
                    </div>
                </ReactCardFlip>
            </div>
        );
    }

    calcPosition() {
        if (this.myRef.current) {
            console.log(`x: ${this.myRef.current.offsetWidth}, y: ${this.myRef.current.offsetHeight}`);
            this.setState({
                positionX: this.myRef.current.offsetWidth,
                positionY: this.myRef.current.offsetHeight
            });
        }
    }

}
