import React, {Component} from 'react';
import '../stylesheets/style.css';

export default class CharacterCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            loading: true,
            url: props.image,
            name: props.name
        };
    }



    render(){
        let image = new Image();
        image.src = this.state.url;
        let styles = {
            width: image.width / 10 + "vw",
            height: image.height / 10 + "vw"
        };
        console.log(styles);
        return (<img src={this.state.url} style={styles}></img>);
    }
}
