const initialState = {
    key: "Andy",
    cardsFlipState: {
        Andy: false,
        Michael: false,
        Pam: false,
        Dwight: false,
        Jim: false,
        
    }
}

  
export default function (state = initialState, action){
    switch (action.type){
        case 'FLIP_CARD':
            return {
                key: state.key,
                isFlipped: !state.isFlipped
            };
        case 'SELECT':
            return {
                key: action.payload,
                isFlipped: state.isFlipped
            };
        default:
            return state;
    }
}
