import React, {Component} from 'react';
import '../stylesheets/style.css';
import {setNewImage} from '../actions/index';
import { bindActionCreators } from 'redux';

export default class CharacterCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            url: props.url,
            name: props.name
        };
    }



    render(){
        let image = new Image(this.state.url);
        console.log(`width: ${image.clientwidth} height ${image.clientHeight}`)
        let style = {
            width: '200px',
            height: '200px'
        }
        return (
            <img src={this.state.url} style = {style}></img>
        );
    }
}
