import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import ReactCardFlip from 'react-card-flip';
import '../stylesheets/style.css';

export default class CharacterCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            isFlipped: false,
            isHovered: false,
            url: props.image,
            name: props.name,
            self: null
        };
        this.handleClick = this.handleClick.bind(this);
        this.handleHover = this.handleHover.bind(this);
        this.getStyle = this.getStyle.bind(this);
    }

    handleClick(e) {
        e.preventDefault();
        this.setState(prevState => (
            { isFlipped: !prevState.isFlipped }
        ));
    }    
    handleHover(){
        this.setState(prevState => ({
            isHovered: !prevState.isHovered
        }));
    }

    componentDidMount() {
        this.setState(prevState => ({
            self: ReactDOM.findDOMNode(this).getBoundingClientRect()
        }));
    }
    
    getStyle(){
        if (this.state.isHovered){
            return {
                transition: 'transform .2s',
                transform: 'scale(1.2)',
                'z-index': '1'
            }
        } else {
            return {
                transition: 'transform .2s',
                transform: 'scale(1)' 
            }
        }
    }

    render(){
        /*
        let imgClass = "hover";
        if (!this.state.isHovered  ){
            imgClass = "unhover";
        } 

        let styles = {};
        if (this.state.isFlipped){
            styles = {
                transition: 'transform .2s',
                transform: 'translate(0vw, 1vh)',
            };
        }
        */

    
        return (
            <div className={imgClass} onClick = {this.handleClick} style={styles} onMouseEnter={this.handleHover} onMouseLeave={this.handleHover}>
                <ReactCardFlip  isFlipped={this.state.isFlipped} flipDirection="horizontal">
                    <div key='front'>
                        <img alt={this.state.name} src={this.state.url} id='characterImage'></img>
                    </div>
                    <div id='back' key='back'>
                        <h1>Dwight</h1>
                    </div>
                </ReactCardFlip>
            </div>
        );
    }

    calcPosition() {
    }

}
