import React, {Component} from 'react';
import ReactCardFlip from 'react-card-flip';
import '../stylesheets/style.css';

export default class CharacterCard extends Component {
    constructor(props){
        super(props);
        this.state = {
            isFlipped: false,
            url: props.image,
            name: props.name
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(e) {
        e.preventDefault();
        this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
    }    

    render(){
        let image = new Image();
        image.src = this.state.url;
        let styles = {
            width: image.width / 42 + "vw",
            height: image.height / 42 + "vw"
        };
       
        return (

            <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="horizontal">
                <div key='front' onClick={this.handleClick}>
                    <img alt={this.state.name} src={this.state.url} style={styles}></img>
                </div>
                <div key='back' onClick={this.handleClick}>
                    <h1>Dwight</h1>
                </div>
            </ReactCardFlip>
 
        );
    }
    
    flip(){
        if (this.state.front){
            this.front.current.classList.toggle("flipped");
            this.back.current.classList.toggle("flipped");
        } else {
            this.back.current.classList.toggle("flipped");
            this.back.current.classList.toggle("flipped");
        }

        this.setState({
            front: !this.state.front,
            url: this.state.url,
            name: this.state.name
        })
       
    }
}
