import React, {Component} from 'react';
import '../stylesheets/style.css';
import {setNewImage} from '../actions/index';
import { bindActionCreators } from 'redux';

export default class CharacterCard extends Component {
    constructor(props){
        super();
        this.setState({
            url: props.url,
            name: props.name
        });
    }



    render(){
        let image = new Image(this.state.url);
        let style = {
            width: image.clientWidth,
            height: image.clientHeight
        }
        return (
            <image href={this.state.url} style = {style}></image>
        );
    }
}
